import "./Form.css";
import FieldText from "../FieldText/FieldText";
import ListaOpciones from "../ListaOpciones/ListaOpciones";
import Button from "../Button/Button";
import { useState } from "react";

const Form = () => {
  const [name, setName] = useState("");
  const [position, setPostition] = useState("");
  const [photo, setPhoto] = useState("");

  const handleSend = (e) => {
    e.preventDefault();
  };
  return (
    <section className="form">
      <form onSubmit={handleSend}>
        <h2>Fill out the form to create a collaborator</h2>
        <FieldText
          title="Name"
          placeholder="Enter name"
          required
          value={name}
        />
        <FieldText title="Position" placeholder="Enter position" required />
        <FieldText title="Photo" placeholder="Enter link to photo" required />
        <ListaOpciones title="Team" placeholder="Select team..." />
        <Button text="Create" />
      </form>
    </section>
  );
};

export default Form;
